import React from 'react'
import { withApollo } from '@apollo/react-hoc'
import { useQuery } from '@apollo/react-hooks'
import { gql } from 'apollo-boost'
import { Spin } from 'antd'
import CharacterCard from './Components/CharacterCard'



const charactersQuery = gql`
  {
    characters {
      name
      image
      house
    }
  }
`

function Page({ client }) {

  const {
    data,
    loading,
    error
  } = useQuery(charactersQuery, { client })

  return (
    <div style={{display: "flex", alignItems: "flex-start", justifyContent: "space-around", width: "80%", flexWrap: "wrap", margin: "60px auto"}}>
      {data ? data.characters.map((item, idx) => <CharacterCard key={idx} name={item.name} image={item.image} house={item.house} loading={loading} />) : <Spin/>}
    </div>
  )
}

export default withApollo(Page)
