import React from 'react'
import { Card, Icon, Avatar } from 'antd'

const { Meta } = Card

const randomColor = "#"+((1<<24)*Math.random()|0).toString(16)

export default function CharacterCard(
    {
        name,
        initials,
        url,
        loading
    }
) {

    return (
        <Card
            loading={loading}
            hoverable
            style={{ width: 240, marginTop: "10px" }}
            cover={<Avatar shape="square" size={64} style={{ backgroundColor: randomColor, verticalAlign: 'middle' }} size="large">{initials}</Avatar>}
        >
            <Meta title={name} description={url} />
        </Card>
    )
}
